import * as React from 'react';
import {
    Text,
    View,
    StyleSheet,
    TextInput,
    ImageBackground,
    TouchableOpacity,
    Image,
    ActivityIndicator
} from 'react-native';
import styles from './styles/testScreen.style.js';
const UserInfoTab = () => {
    const [isLoading, setLoading] = useState(true);
    const [data, setData] = useState([]);
    useEffect(() => {
        var id = { "id": savedID };
        console.log(savedID);
        console.log(id);
        /*
        If testing this on a real device, uncomment this code
        Caused by lack of support from expo-secure-store for web clients,
        should work on mobile
        
        retrieveTokenAsync()
            .then(result => {
                console.log("Retrieving: ", result);
                fetch('http://localhost:3000/api/user/user', {
                    method: 'POST',
                    headers: {
                        'Authorization': `Bearer ${accessToken}`,
                        'Content-Type': 'application/json'
                    },

                    body: JSON.stringify(id)

                })
                    .then((response) => response.json())
                    .then((json) => {
                        console.log('JSON here we go babey: ');
                        console.log(json);
                        setData(json);
                    })
                    .catch((error) => {
                        console.error(error)
                    })
                    .finally(() => setLoading(false));
            })
            .catch(error => {
                console.log("Error in promise object retrieveTokenAsync():::", error);
            });
            */
        /* Uncomment this code if testing on Web */

        fetch('http://localhost:3000/api/user/user', {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${accessToken}`,
                'Content-Type': 'application/json'
            },

            body: JSON.stringify(id)

        })
            .then((response) => response.json())
            .then((json) => {
                console.log('JSON here we go babey: ');
                console.log(json);
                setData(json);
            })
            .catch((error) => {
                console.error(error)
            })
            .finally(() => setLoading(false));
        /* */

    }, []);

    return (
        console.log(data),
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            <Text >
                User Info Placeholder
            </Text>
            {isLoading ? <ActivityIndicator /> : (
                <View>

                    <Text>Device ID: {data.device_id}</Text>
                    <Text>User Type: {data.user_type}</Text>
                    <Text>Email: {data.email}</Text>
                    <Text>Password: {data.passwrd}</Text>
                </View>

            )}
        </View>

    );
}

export default UserInfoTab;
