import 'react-native-gesture-handler';
import React, { useState } from 'react';
import {
    Text,
    View,
    StyleSheet,
    TextInput,
    ImageBackground,
    TouchableOpacity,
    Image,
    AsyncStorage
} from 'react-native';
import {
    NavigationContainer,

} from '@react-navigation/native';
import {
    createStackNavigator
} from '@react-navigation/stack';
import {
    createBottomTabNavigator
} from '@react-navigation/bottom-tabs';


import Constants from 'expo-constants';
import { State } from 'react-native-gesture-handler';
const AuthContext = React.createContext();

const LoginScreen = ({ navigation }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const { signIn } = React.useContext(AuthContext);
    return (

        <View style={styles.container}>
            <ImageBackground source={require("./assets/images/backgroundLogin.jpg")} style={styles.backgroundImage}>
                <View style={styles.formContainer}>
                    <Image source={require("./assets/images/testIllya.jpg")} style={styles.logo} />
                    <View style={styles.formElement}>
                        <Text style={styles.paragraph}> Email</Text>
                    </View>
                    <View style={styles.formElement}>
                        <View style={styles.inputView}>
                            <TextInput style={styles.input}
                                placeholder="johndoe@gmail.com"
                                placeholderTextColor='gray'
                                onChangeText={(email) => setEmail}
                            />
                        </View>
                    </View>
                    <View style={styles.formElement}>
                        <Text style={styles.paragraph}> Password</Text>
                    </View>
                    <View style={styles.formElement}>
                        <View style={styles.inputView}>
                            <TextInput style={styles.input}
                                placeholder="Password"
                                placeholderTextColor='gray'
                                secureTextEntry={true}
                                onChangeText={(password) => setPassword}
                            />
                        </View>
                    </View>
                    <View style={styles.formElement}>
                        <TouchableOpacity style={styles.loginButton}
                            onPress={() => signIn({email, password})}
                        >
                            <Text style={styles.buttonText}> Login </Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </ ImageBackground>
        </View>

    );
}
const HotspotScreen = () => {
    return (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            <Text >
                Hotspot Map Placeholder
            </Text>
        </View>

    );

}
const Tab = createBottomTabNavigator();


const Stack = createStackNavigator();

/*
https://reactnavigation.org/docs/auth-flow/
Modified and expanded upon code from the documents on React Navigation
to use in this project below
*/

const App = ({ navigation }) => {
    const [state, dispatch] = React.useReducer(
        (prevState, action) => {
            switch (action.type) {
                case 'RESTORE_TOKEN':
                    return {
                        ...prevState,
                        userToken: action.token,
                        isLoading: false,
                    };
                case 'SIGN_IN':
                    return {
                        ...prevState,
                        isSignout: false,
                        userToken: action.token,
                    };
                case 'SIGN_OUT':
                    return {
                        ...prevState,
                        isSignout: true,
                        userToken: null,
                    };
            }
        },
        {
            isLoading: true,
            isSignout: false,
            userToken: null,
        }
    );
    React.useEffect(() => {
        const bootstrapAsync = async () => {
            let userToken;
            try {
                userToken = await AsyncStorage.getItem('userToken');

            } catch (e) {
                //restore failed token later once I know what I am doing fully
            }
            //Validate token here
            dispatch({ type: 'RESTORE_TOKEN', token: userToken });
        };
        bootstrapAsync();
    }, []);
    const authContext = React.useMemo(
        () => ({
            signIn: async data => {
                //send sign in data here
                dispatch({ type: 'SIGN_IN', token: 'dummy-auth-token' });
            },
            signOut: () => dispatch({ type: 'SIGN_OUT' }),
            signUp: async data => {
                dispatch({ type: 'SIGN_IN', token: 'dummy-auth-token' });
            }
        }),
        []
    );



    return (

        <AuthContext.Provider value={authContext}>
            <NavigationContainer>


                <Stack.Navigator>
                    {
                        State.isLoading ? (
                            <Stack.Screen
                                name="LoadingScreen"
                                component={LoadingScreen}
                            />
                        ) : State.userToken == null ? (
                            // No authenticated token
                            <Stack.Screen
                                name="Login"
                                component={LoginScreen}
                                options={{
                                    headerShown: false
                                }}
                            />
                        ) : (
                                    <Stack.Screen
                                        name="Hotspot"
                                        component={HotspotScreen}
                                    />
                                )
                    }
                </Stack.Navigator>
            </NavigationContainer>
        </AuthContext.Provider>
    );
    /*
        <NavigationContainer>

            <Stack.Navigator
                screenOptions={{ headerShown: false }}
            >
                <Stack.Screen

                    name="Login"
                    component={LoginScreen}

                />
                <Stack.Screen name="Hotspot"
                    component={HotspotScreen}
                />

            </Stack.Navigator>


        </NavigationContainer>
        */

}
const Home = () => {
    return (

        <Tab.Navigator>
            <Tab.Screen
                name="Hotspot"
                component={HotspotScreen}
            />
        </Tab.Navigator>

    );

}
export default App;
const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        flexDirection: 'column',
        backgroundColor: 'white',
        alignItems: 'stretch'
    },
    formContainer: {
        flex: 1,
        justifyContent: 'center',
        flexDirection: 'column',
        paddingBottom: 100,
        alignItems: 'stretch'
    },
    formElement: {
        paddingLeft: 40,
        paddingRight: 40,
        paddingTop: 20,
        paddingBottom: 20
    },
    backgroundImage: {
        flex: 1,
        resizeMode: 'cover',
        justifyContent: 'center',
    },
    inputView: {
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'stretch',
    },
    input: {
        fontSize: 24,
        borderBottomWidth: 1,
        borderColor: 'gray',

    },
    loginButton: {
        borderRadius: 25,
        height: 50,
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: 30,
        marginBottom: 30,
        backgroundColor: 'blue',
    },
    buttonText: {
        color: 'white',

        fontSize: 30,
    },
    titles: {
        fontSize: 18,
        fontWeight: 'bold',
        textAlign: 'center',
        justifyContent: 'center',
    },
    paragraph: {

        fontSize: 18,
        color: 'white',
        textAlign: 'left',
        justifyContent: 'center',
    },

    logo: {
        width: 100,
        height: 100,
        resizeMode: 'contain',
        alignSelf: 'center',
        margin: 100
    }
});
