import 'react-native-gesture-handler';
import React, { useState } from 'react';
import {
    Text,
    View,
    StyleSheet,
    TextInput,
    ImageBackground,
    TouchableOpacity,
    Image
} from 'react-native';
import {
    NavigationContainer
} from '@react-navigation/native';
import {
    createStackNavigator
} from '@react-navigation/stack';


import Constants from 'expo-constants';
const background = { uri: "/assets/images/backgroundLogin.jpg" };
const logo = { uri: "/assets/images/testIllya.jpg" }

const LoginScreen = ({navigation}) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    return (

            <View style={styles.container}>
                <ImageBackground source={require("./assets/images/backgroundLogin.jpg")} style={styles.backgroundImage}>
                    <View style={styles.formContainer}>
                        <Image source={require("./assets/images/testIllya.jpg")} style={styles.logo} />
                        <View style={styles.formElement}>
                            <Text style={styles.paragraph}> Email</Text>
                        </View>
                        <View style={styles.formElement}>
                            <View style={styles.inputView}>
                                <TextInput style={styles.input}
                                    placeholder="johndoe@gmail.com"
                                    placeholderTextColor='gray'
                                    onChangeText={(email) => setEmail}
                                />
                            </View>
                        </View>
                        <View style={styles.formElement}>
                            <Text style={styles.paragraph}> Password</Text>
                        </View>
                        <View style={styles.formElement}>
                            <View style={styles.inputView}>
                                <TextInput style={styles.input}
                                    placeholder="Password"
                                    placeholderTextColor='gray'
                                    secureTextEntry={true}
                                    onChangeText={(password) => setPassword}
                                />
                            </View>
                        </View>
                        <View style={styles.formElement}>
                            <TouchableOpacity style={styles.loginButton}
                                onPress={() => navigation.navigate('Hotspot')}
                            >
                                <Text style={styles.buttonText}> Login </Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                </ ImageBackground>
            </View>

    );
}
const HotspotScreen = () => {
    return (
        <Text>
            Hotspot Map Placeholder
        </Text>
    );

}

const Stack = createStackNavigator();

const App = () => {
    return(

        <NavigationContainer>
            <Stack.Navigator>
                <Stack.Screen 
                    options= {{
                        headerShown: 'false'
                    }}
                    name="Login"
                    component={LoginScreen}
                    
                    />
                <Stack.Screen name="Hotspot"
                    component={HotspotScreen}
                />
                
            </Stack.Navigator>
                
        </NavigationContainer>
    );
}
export default App;
const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        flexDirection: 'column',
        backgroundColor: 'white',
        alignItems: 'stretch'
    },
    formContainer: {
        flex: 1,
        justifyContent: 'center',
        flexDirection: 'column',
        paddingBottom: 100,
        alignItems: 'stretch'
    },
    formElement: {
        paddingLeft: 40,
        paddingRight: 40,
        paddingTop: 20,
        paddingBottom: 20
    },
    backgroundImage: {
        flex: 1,
        resizeMode: 'cover',
        justifyContent: 'center',
    },
    inputView: {
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'stretch',
    },
    input: {
        fontSize: 24,
        borderBottomWidth: 1,
        borderColor: 'gray',

    },
    loginButton: {
        borderRadius: 25,
        height: 50,
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: 30,
        marginBottom: 30,
        backgroundColor: 'blue',
    },
    buttonText: {
        color: 'white',

        fontSize: 30,
    },
    titles: {
        fontSize: 18,
        fontWeight: 'bold',
        textAlign: 'center',
        justifyContent: 'center',
    },
    paragraph: {

        fontSize: 18,
        color: 'white',
        textAlign: 'left',
        justifyContent: 'center',
    },

    logo: {
        width: 100,
        height: 100,
        resizeMode: 'contain',
        alignSelf: 'center',
        margin: 100
    }
});
