import React, { useState, useEffect } from 'react';
import styles from './styles/loginScreenStyle';
import {
    Text,
    View,
    StyleSheet,
    TextInput,
    ImageBackground,
    TouchableOpacity,
    Image,
    ActivityIndicator
} from 'react-native';
import tokenDispatch from '../hooks/tokenDispatch';


const LoginScreen = () => {
    const AuthContext = React.createContext();
    tokenDispatch();
    const [id, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const { signIn } = React.useContext(AuthContext);
    const authContext = React.useMemo(
        () => (

            {

                signIn: async data => {
                    //send sign in data here
                    savedID = data.id

                    fetch('http://localhost:3000/api/auth/login', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json'
                        },
                        body: JSON.stringify(data),
                    })
                        .then(response => response.json())
                        .then(data => {
                            saveTokenAsync(data.accessToken);
                            accessToken = data.accessToken;
                            console.log("Success: ", data);

                        })
                        .catch((error) => {
                            console.error('Error: ', error);
                        });
                    dispatch({ type: 'SIGN_IN', token: accessToken });
                },
                signOut: () => dispatch({ type: 'SIGN_OUT' }),
                signUp: async data => {
                    dispatch({ type: 'SIGN_IN', token: 'dummy-auth-token' });
                },
            }),
        []
    );
    return (
        
        <View style={styles.container}>
            {/*<ImageBackground source={require("./assets/images/backgroundLogin.jpg")} style={styles.backgroundImage}>*/}
            <View style={styles.formContainer}>
                <Image source={require("../assets/images/testIllya.jpg")} style={styles.logo} />
                <View style={styles.formElement}>
                    <Text style={styles.paragraph}> Email</Text>
                </View>
                <View style={styles.formElement}>
                    <View style={styles.inputView}>
                        <TextInput style={styles.input}
                            placeholder="johndoe@gmail.com"
                            placeholderTextColor='gray'
                            value={id}
                            onChangeText={setEmail}
                        />
                    </View>
                </View>
                <View style={styles.formElement}>
                    <Text style={styles.paragraph}> Password</Text>
                </View>
                <View style={styles.formElement}>
                    <View style={styles.inputView}>
                        <TextInput style={styles.input}
                            placeholder="Password"
                            placeholderTextColor='gray'
                            secureTextEntry={true}
                            value={password}
                            onChangeText={setPassword}
                        />
                    </View>
                </View>
                <View style={styles.formElement}>
                    <TouchableOpacity style={styles.loginButton}
                        onPress={() => signIn({ id, password })}
                    >
                        <Text style={styles.buttonText}> Login </Text>
                    </TouchableOpacity>
                </View>
            </View>
            {/*</ ImageBackground> */}
        </View>

    );
}

export default LoginScreen;
